mapper.update:

UPDATE checkorder  
   SET orderstate  = #{orderstate,jdbcType=VARCHAR}, 
       remark      = #{remark,jdbcType=VARCHAR}    
 WHERE idno   = #{idno,jdbcType=CHAR}
   AND roomno = #{idno,jdbcType=INTEGER};

mapper.select:

		select * from checkorder 
		        WHERE idno   = #{idno,jdbcType=CHAR}
                  AND roomno = #{idno,jdbcType=INTEGER};


		select * from checkorder 
		 WHERE idno   = #{idno,jdbcType=CHAR}
           AND roomno = #{idno,jdbcType=INTEGER}
           AND ordertype = "预约单" 
           AND orderstate IN ("已付","未付") 
           ORDER BY  orderno;
           ;
